package com.day5;

public class Artimethic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7;
		int b=5;
		int c;
		
		c= a+b;
		System.out.println(c); 
		c= a-b;
		System.out.println(c);
		c= a/b;
		System.out.println(c);
		c=a%b;
		System.out.println(c);
		a= a++;
		System.out.println(a);
		
		System.out.println(c<a);
		

	}

}
