package MorePractice;

public class IfContinued {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int ticketPrice =10;
		int age = 25 ;
		boolean isStudent = true ;
		
		if (age <=15) {
			ticketPrice = 5;
		}else if (age >60) {
			ticketPrice = 5;
		}else if (isStudent) {
			ticketPrice = 5;
		
		System.out.println(ticketPrice);
		}
		
				

	}

}
