package com.icraft.day4;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           
		byte myNum = 19; // byte integer
		short myValue = 10; // short integer
		int noOfRooms = 200; // Integer (whole number)
		long distanceToSun = 98000000L; // long integer
		
		double weightofpotato = 5.984; // double data type can take fractions 
		float myFloarNum = 5.99f; // floating point number
		
		char myLetter = 'D'; // Character must be in single quotes 
		boolean myBool = true; // Boolean 
		
		System.out.println("byte: " + myNum);
		System.out.println("short: " + myValue);
		System.out.println("int: " + noOfRooms);
		System.out.println("Long: " + distanceToSun);
		System.out.println("double: " + weightofpotato);
		System.out.println("float: " + myFloarNum);
		System.out.println("char: " + myLetter);
		System.out.println("boolean: " + myBool);
		
		
	}

}
