package arthimethicOperator;

public class arthimethicOperator1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a,b,c;
		a = 10;
		b = 2;
		c = a+b;
		
		System.out.println("Addition: " + c);//12
		c = a - b;
		System.out.println("Subtraction: " + c);//8
		c = a*b;
		System.out.println("Multiplication: " + c);//20
		c = a/b;
		System.out.println("Division: " + c);//5
		
//		b = 3;
//		a = c%b;
//		System.out.println("Addition: " + c);//1
	}

}
