package com.icraftjava;

public class EvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		while(i<50) {
			System.out.print(i + " ");
			i+=2;
		}
		System.out.println();
		
		for(int k=0; k<50; k++) {
			if (k%2==1) {
		
			System.out.print(k + " ");}
		}
		System.out.println();
	}
}

		
		
			
			
		



	
		
		
