package trueorFalse2;

public class TrueFalse2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a, b;
		a = 40;
		b = 30;
		System.out.println("a == b = " + (a == b));//False
		System.out.println("a! = b = " + (a!= b));//True
		System.out.println("a > b = " + (a > b));//True
		System.out.println("a < b = " + (a < b));//False
		System.out.println("a >= b = " + (a >= b));//True
		System.out.println("a <= b = " + (a <= b));//False
				

	}

}
