package com.icraftjava;

public class Swiotch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day=5;
		String DayName;
		switch (day) {
		case 1:
			DayName = "Today is Monday";
			break;
	 
		case 2:
			DayName = "Today is Tuesday";
			break;
		
		case 3:
			DayName = "Today is wedensday";
			break;
		default:
			DayName = "Invalid Day!";
			break;
		}
		System.out.println(DayName);
	}
}
		
	
	


